import React, { useState } from 'react';
import axios from 'axios';

function MovieList() {
  const [movies, setMovies] = useState([]);
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [actors, setActors] = useState([]);

  const handleMovieClick = (movieTitle) => {
    setSelectedMovie(movieTitle);
    axios.get(`http://localhost:3001/movies/actors?title=${movieTitle}`)
      .then((response) => {
        setActors(response.data);
      })
      .catch((error) => {
        console.error(`Error fetching actors: ${error}`);
      });
  };

  const fetchMovies = () => {
    axios.get('http://localhost:3001/movies/two-actors')
      .then((response) => {
        setMovies(response.data);
      })
      .catch((error) => {
        console.error(`Error fetching movies: ${error}`);
      });
  };

  return (
    <div>
      <h2>Movies with two or more actors:</h2>
      <ul>
        {movies.map((movie) => (
          <li key={movie.Mov_Title} onClick={() => handleMovieClick(movie.Mov_Title)}>
            {movie.Mov_Title}
          </li>
        ))}
      </ul>
      {selectedMovie && (
        <div>
          <h2>Actors in {selectedMovie}:</h2>
          <ul>
            {actors.map((actor) => (
              <li key={actor.Act_Name}>{actor.Act_Name}</li>
            ))}
          </ul>
        </div>
      )}
      <button onClick={fetchMovies}>Fetch Movies</button>
    </div>
  );
}

export default MovieList;
