import React, { useState, useEffect } from 'react';
import axios from 'axios';

const BASE_URL = 'http://localhost:3001';

const App = () => {
  const [movies, setMovies] = useState([]);
  const [actors, setActors] = useState([]);
  const [ratings, setRatings] = useState([]);
  const [error, setError] = useState(null);

  // Load data on component mount
  useEffect(() => {
    // Fetch movies directed by 'Hitchcock'
    axios.get(`${BASE_URL}/movies/hitchcock`)
      .then(response => setMovies(response.data))
      .catch(error => setError(error.response.data));

    // Fetch movies with multiple actors
    axios.get(`${BASE_URL}/movies/multiple-actors`)
      .then(response => setMovies(response.data))
      .catch(error => setError(error.response.data));

    // Fetch actors who acted in a movie before 2000 and also in a movie after 2015
    axios.get(`${BASE_URL}/actors/2000-2015`)
      .then(response => setActors(response.data))
      .catch(error => setError(error.response.data));

    // Fetch movie ratings
    axios.get(`${BASE_URL}/movies/ratings`)
      .then(response => setRatings(response.data))
      .catch(error => setError(error.response.data));
  }, []);

  // Handle update rating click
  const handleUpdateRating = () => {
    axios.put(`${BASE_URL}/movies/spielberg`)
      .then(() => {
        // Refresh movie ratings
        axios.get(`${BASE_URL}/movies/ratings`)
          .then(response => setRatings(response.data))
          .catch(error => setError(error.response.data));
      })
      .catch(error => setError(error.response.data));
  };

  return (
    <div>
      <h1>Movies Directed by Hitchcock</h1>
      {movies.length > 0 ? (
        <ul>
          {movies.map(movie => <li key={movie.Mov_id}>{movie.Mov_Title}</li>)}
        </ul>
      ) : (
        <p>No movies found.</p>
      )}

      <h1>Movies with Multiple Actors</h1>
      {movies.length > 0 ? (
        <ul>
          {movies.map(movie => <li key={movie.Mov_id}>{movie.Mov_Title}</li>)}
        </ul>
      ) : (
        <p>No movies found.</p>
      )}

      <h1>Actors Who Acted in a Movie Before 2000 and After 2015</h1>
      {actors.length > 0 ? (
        <ul>
          {actors.map(actor => <li key={actor.Act_id}>{actor.Act_Name}</li>)}
        </ul>
      ) : (
        <p>No actors found.</p>
      )}

      <h1>Movie Ratings</h1>
      {ratings.length > 0 ? (
        <ul>
          {ratings.map(rating => (
            <li key={rating.Mov_id}>
              {rating.Mov_Title}: {rating.Max_Stars} stars
            </li>
          ))}
        </ul>
      ) : (
        <p>No ratings found.</p>
      )}

      <button onClick={handleUpdateRating}>Update Ratings for Spielberg Movies</button>

      {error && (
        <div>
          <h2>Error</h2></div> )}
</div>)}
