import React, { useState, useEffect } from 'react';
import axios from 'axios';
import MovieSearch from './Components/MovieSearch/MovieSearch';
import MovieList from './Components/MultipleActorsInMovie/MovieList';
import './App.css';
import Actors2000And2015 from './Components/Actors2000And2015/Actors2000And2015';
import MovieRatings from './Components/MovieRatings/MovieRatings';

const BASE_URL = 'http://localhost:3001';

const App = () => {
  const [actorsByYear, setActorsByYear] = useState([]);
  const [ratings, setRatings] = useState([]);
  const [error, setError] = useState(null);
  const [message, setMessage] = useState('');

  // Load data on component mount
  useEffect(() => {
    // Fetch movie ratings
    axios
      .get(`${BASE_URL}/movies/ratings`)
      .then((response) => setRatings(response.data))
      .catch((error) => setError(error.response.data));
  }, []);

  const handleUpdateRatings = () => {
    axios
      .put('http://localhost:3001/movies/quentin_tarantino')
      .then((response) => setMessage(response.data))
      .catch((error) => console.error(error));
  };

  return (
    <div className="App">
      <>
        <MovieSearch />
        <MovieList />
        <Actors2000And2015 />
        <MovieRatings/>

        <div>
          <h1>Movie Database</h1>
          <button onClick={handleUpdateRatings}>
            Update Quentin Tarantino Ratings
          </button>
          <p>{message}</p>
        </div>
      </>
    </div>
  );
};
export default App;
